;; **
;;; # A Little Bit of Gambling
;; **

;; @@
(ns a-bit-of-gambling
  (:require [gorilla-plot.core :as plot])
  (:use clojure.repl
        [anglican
         core 
         [runtime :exclude [distribution]] 
         emit
         [state :only [get-predicts get-log-weight]]]
        distribution
        [anglib crp]
        [clojure.string :only (join split blank?)]))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; @@

(def a-little-bit-of-gambling 
  (distribution [] 
   {:algorithm :pcascade :number-of-threads 500 :number-of-paricles 10}
   (let [coin (sample (flip 0.4))
         first-ball-color (sample (categorical [['red 0.3] 
                                         ['blue 0.7]]))
         second-ball-color (sample (categorical [['red 0.2] 
                                          ['green 0.3] 
                                          ['blue 0.5]]))
         win (or (and (= coin true)
                      (or (= first-ball-color 'red) 
                          (= second-ball-color 'red)))
                 (= first-ball-color second-ball-color))]
     (predict :win win))))
         
(defn bool-to-int [x] (if x 1 0))
(plot/histogram
  (->> (repeatedly 1000 #(sample a-little-bit-of-gambling))
     (map :win)
     (map bool-to-int))
      :normalize :probability :bins 100)
