;; **
;;; ## Parameter Estimation
;;; 
;;; We will now modify our original model to perform parameter estimation. One of the advantages of probabilistic programming systems is that it is almost trivial to encode assumptions about the data. In this case we take the rotation-matrix form for @@\v{A}@@ and the diagonal matrix form for @@\v{Q}@@, and directly substitute them into the program code, allowing us to reduce the complexity of the problem. Instead of estimating the full matrices @@\v{A}@@ and @@\v{Q}@@, we only estimate @@\omega@@ and @@q@@
;;; 
;;; @@ \begin{align}
;;;     \v{A}
;;;     &=
;;;     \left[
;;;         \begin{array}{cc}
;;;             \cos \omega
;;;             &
;;;             -\sin \omega
;;;             \\\\
;;;             \sin \omega
;;;             &
;;;             \cos \omega
;;;         \end{array}
;;;     \right]
;;;     &
;;;     \v{Q}
;;;     &=
;;;     q
;;;     \v{I}\_2
;;; \end{align} @@
;;; 
;;; 
;;; 
;; **

;; @@
(with-primitive-procedures [mmul mul add sub shape matrix identity-matrix zero-vector rotation-matrix]
  (defquery kalman-params
    "A basic Kalman smoother. Predicts a state sequence from the posterior
    given observations"
    [observations obs-matrix obs-cov init-mean init-cov]
    (let [;; D is dimensionality of data,
          ;; K is dimensionality of latent space
          [D K] (shape obs-matrix)
          ;; prior on observation noise
          obs-dist (mvn (zero-vector D) obs-cov)
          ;; prior on initial state
          start-dist (mvn init-mean init-cov)
          ;; sample transition matrix
          omega (sample (gamma 1.0 10.0))
          trans-matrix (rotation-matrix omega)
          ;; sample transition covariance
          q (sample (gamma 1.0 100.0))
          trans-cov (mul (identity-matrix K K) q)
          ;; prior on transition noise
          trans-dist (mvn (zero-vector K) trans-cov)]
      ;; loop over observations
      (reduce (fn [states obs]
                  (let [;; sample next state
                        prev-state (peek states)
                        state (if prev-state
                         	      (add (mmul trans-matrix prev-state)
                                       (sample trans-dist))
                                  (sample start-dist))]
                    ;; observe next data point (when available)
                    (observe (count states) obs-dist (sub (mmul obs-matrix state) obs))
                    ;; append state to sequence and continue with next obs
                    (conj states state)))
                ;; start with empty sequence
                []
                ;; loop over data
                observations)
      ;; output parameters
  	  (predict :omega omega)
      (predict :q q))))

;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/kalman-params</span>","value":"#'kalman/kalman-params"}
;; <=

;; @@
(def smc-param-samples
  (->> (doquery :smc
             kalman-params
             [+observations+ 
              +obs-matrix+ +obs-cov+ 
              +init-mean+ +init-cov+]
             :number-of-particles 10000)
	   (take 10000)
       doall
       time))
