;; **
;;; ## Inference of State Sequence
;;; 
;;; ### Probabilistic Program
;;; 
;; **

;; @@
(with-primitive-procedures [mmul add sub shape matrix identity-matrix zero-vector]
  (defquery kalman
    "A basic Kalman smoother. Predicts a state sequence from the posterior
    given observations"
    [observations obs-matrix obs-cov trans-matrix trans-cov init-mean init-cov]
    (let [;; D is dimensionality of data,
          ;; K is dimensionality of latent space
          [D K] (shape obs-matrix)
          ;; prior on observation noise
          obs-dist (mvn (zero-vector D) obs-cov)
          ;; prior on initial state
          start-dist (mvn init-mean init-cov)
          ;; prior on transition noise
          trans-dist (mvn (zero-vector K) trans-cov)]
	(predict :states
      (matrix 
        (reduce (fn [states obs]
                  (let [;; sample next state
                        prev-state (peek states)
                        state (if prev-state
                         	      (add (mmul trans-matrix prev-state)
                                       (sample trans-dist))
                                  (sample start-dist))]
                    ;; observe next data point (when available)
                    (observe (count states) obs-dist (sub (mmul obs-matrix state) obs))
                    ;; append state to sequence and continue with next obs
                    (conj states state)))
                ;; start with empty sequence
                []
                ;; loop over data
                observations))))))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/kalman</span>","value":"#'kalman/kalman"}
;; <=

;; **
;;; ### Sequential Monte Carlo Inference
;; **

;; @@
(def smc-samples
  (->> (doquery :smc
             kalman
             [+observations+ 
              +obs-matrix+ +obs-cov+ 
              +trans-matrix+ +trans-cov+ 
              +init-mean+ +init-cov+]
             :number-of-particles 10000)
	   (take 10000)
       doall
       time))
;; @@
;; ->
;;; &quot;Elapsed time: 26221.365 msecs&quot;
;;; 
;; <-
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/smc-samples</span>","value":"#'kalman/smc-samples"}
;; <=

;; **
;;; ### Calculate sample average of posterior mean and variance 
;; **

;; @@
(defn expected-value
  [v-fn samples]
  (let [vs (map v-fn samples)
        lws (map get-log-weight samples)
        vlws (map vector vs lws)
        max-lw (reduce max lws)]
    (loop [vlws vlws
           sum-wv 0.0
           sum-w 0.0]
      (if-let [[v lw] (first vlws)]
        (let [w (exp (- lw max-lw))]
          (recur (rest vlws)
                 (add sum-wv (mul w v))
                 (add sum-w w)))
        (div sum-wv sum-w)))))

(defn square [x] 
  (mul x x))

(defn empirical-moments 
  [samples]
  (let [mean (expected-value 
               (comp :states get-predicts) 
               samples)
        var (sub (expected-value 
                   (comp square :states get-predicts) 
                   samples)
		         (mul mean mean))]
    {:mean mean
     :var var}))

(def smc-moments
  (empirical-moments smc-samples))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/smc-moments</span>","value":"#'kalman/smc-moments"}
;; <=

;; **
;;; ### Exact Inference: Forward-Backward Algorithm
;; **

;; @@
;; Parameters (following Matlab implementation by Murphy)
;;
;; http://www.cs.ubc.ca/~murphyk/Software/Kalman/kalman.html
;;
;; m0	init-mean
;; V0	init-cov
;; C 	obs-matrix
;; R	obs-cov    
;; A	trans-matrix
;; Q	trans-cov

;; Filtering notation
;;
;; y	observation y(t)			
;; mf	filtering expectation mf(t) = E[x(t) | y(1:t)]
;; Vf	filtering covariance Vf(t) = Cov[x(t) | y(1:t)]
;; mp 	prior predictive expectation mp(t) = E[x(t) | y(1:t-1)]
;; Vp   prior predictive covariance Vp(t) = Cov[x(t) | y(1:t-1)]

(defn kalman-filter
  [observations 
   obs-matrix obs-cov 
   trans-matrix trans-cov 
   init-mean init-cov]
  (let [;; set-up some shorthands
        C obs-matrix
        Ct (transpose C)
        R obs-cov
        A trans-matrix
        At (transpose A)
        Q trans-cov
        I (apply identity-matrix (shape init-cov))
        z (zero-vector (first (shape obs-matrix)))]
    (loop [ys observations
           mp init-mean
           Vp init-cov
           mfs []
           Vfs []
           cs []]
      (if-let [y (first ys)]
   	    (let [S (add (mmul C (mmul Vp Ct)) R)
              Sinv (inverse S)
              ;; K(t) = Vp(t) Ct (C Cp Ct + R)^-1
          	  K (mmul Vp (mmul Ct Sinv))
              ;; mf(t) = mp(t) + K(t) (y(t) - C mp(t))
          	  e (sub y (mmul C mp))
              mf (add mp (mmul K e))
              ;; Vf(t) = (I - K(t) C) Vp(t)
              Vf (mmul (sub I (mmul K C)) Vp)
              ;; c(t) = N(y(t) | C mp(t), C Vp Ct + R)
              c (observe (mvn z S) e)]
          
          (recur (rest ys)
                 ;; mp(t+1) = A mf(t)
                 (mmul A mf)
                 ;; Vp(t+1) = A Vf At + Q
                 (add (mmul A (mmul Vf At)) Q)
          		 (conj mfs mf)
                 (conj Vfs Vf)
                 (conj cs c)))
        [mfs Vfs cs]))))

;; Backward pass notation
;;
;; ms	smoothing expectation ms(t) = E[x(t) | y(1:T)]
;; Vs	smoothing covariance Vs(t) = Cov[x(t) | y(1:T)]
;; mpp 	predictive expectation mpp(t) = mp(t+1) = E[x(t+1) | y(1:t)]
;;		(shifted by one relative to mp in filtering pass)
;; Vpp  predictive covariance Vpp(t) = Vp(t+1) = Cov[x(t+1) | y(1:t)]
;;		(shifted by one relative to Vp in filtering pass)

(defn kalman-backward
  [trans-matrix trans-cov mfs Vfs]
  (let [;; set-up shorthands
        A trans-matrix
        At (transpose A)
        Q trans-cov]
    (loop [mfs (pop mfs)
           Vfs (pop Vfs)
           mss (list (peek mfs))
           Vss (list (peek Vfs))]
      (if (empty? mfs)
          [mss Vss]
          (let [;; mpp(t) = mp(t+1) = A mf(t)
                mpp (mmul A (peek mfs))
                ;; Vpp(t) = Vp(t+1) = A Vf(t) At + Q
                Vpp (add (mmul A (mmul (peek Vfs) At)) Q)
				;; J(t) = Vf(t) At Vp(t+1)^-1
                J (mmul (peek Vfs) (mmul At (inverse Vpp)))
                Jt (transpose J)
                ;; ms(t) = mf(t) + J (ms(t+1) - mp(t+1))
                ms (add (peek mfs) (mmul J (sub (peek mss) mpp)))
                ;; Vs(t) = Vf(t) + J (Vs(t+1) - Vp(t+1)) Jt
                Vs (add (peek Vfs) (mmul J (mmul (sub (peek Vfs) Vpp) Jt)))]
            (recur (pop mfs)
                   (pop Vfs)
                   (conj mss ms)
                   (conj Vss Vs)))))))

(defn kalman-smoother
  [observations 
   obs-matrix obs-cov 
   trans-matrix trans-cov 
   init-mean init-cov]
  (let [[mfs Vfs cs] (kalman-filter observations
                       obs-matrix obs-cov 
					   trans-matrix trans-cov 
					   init-mean init-cov)
        [mss Vss] (kalman-backward trans-matrix trans-cov mfs Vfs)]
       [mss Vss cs]))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/kalman-smoother</span>","value":"#'kalman/kalman-smoother"}
;; <=

;; **
;;; ### Posterior mean and variance from Kalman smoother
;; **

;; @@
(def true-moments 
  (let [[mean covar _]
          (kalman-smoother +observations+
                	       +obs-matrix+ +obs-cov+
				           +trans-matrix+ +trans-cov+
                 		   +init-mean+ +init-cov+)]
    {:mean mean
     :var (matrix (map mat/diagonal covar))}))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/true-moments</span>","value":"#'kalman/true-moments"}
;; <=

;; **
;;; ## Plot Results
;; **

;; @@
(def +x-lim+ [-1.4 1.4])


(defn plot-moments [moments]
  (let [mean (:mean moments)
        err (mul 2 (mat/sqrt (:var moments)))
        [T K] (shape mean)]
    (plot/compose
     (plot/list-plot (into [] (mat/slice mean 1 0))
                     :joined true
                     :plot-range [:all +x-lim+]
                     :color (first +plot-colors+))
     (plot/list-plot (into [] (mat/slice (add mean err) 1 0))
                     :plot-range [:all +x-lim+]
                     :joined true
                     :color (second +plot-colors+))
     (plot/list-plot (into [] (mat/slice (sub mean err) 1 0))
