;; **
;;; ### Sequential Monte Carlo Inference
;; **

;; @@
(def smc-samples
  (->> (doquery :smc
             kalman
             [+observations+ 
              +obs-matrix+ +obs-cov+ 
              +trans-matrix+ +trans-cov+ 
              +init-mean+ +init-cov+]
             :number-of-particles 10000)
	   (take 10000)
       doall
       time))
