;; **
;;; ### Particle Cascade Inference
;; **

;; @@
(def smc-samples
  (->> (doquery :pcascade
             kalman
             [+observations+ 
              +obs-matrix+ +obs-cov+ 
              +trans-matrix+ +trans-cov+ 
              +init-mean+ +init-cov+]
             :number-of-particles 10
             :number-of-threads 1000)
	   (take 10000)
       doall
       time))
