;; **
;;; ### Calculate sample average of posterior mean and variance 
;; **

;; @@
(defn expected-value
  [v-fn samples]
  (let [vs (map v-fn samples)
        lws (map get-log-weight samples)
        vlws (map vector vs lws)
        max-lw (reduce max lws)]
    (loop [vlws vlws
           sum-wv 0.0
           sum-w 0.0]
      (if-let [[v lw] (first vlws)]
        (let [w (exp (- lw max-lw))]
          (recur (rest vlws)
                 (add sum-wv (mul w v))
                 (add sum-w w)))
        (div sum-wv sum-w)))))

(defn square [x] 
  (mul x x))

(defn empirical-moments 
  [samples k]
  (let [mean (expected-value 
               (comp k get-predicts) 
               samples)
        var (sub (expected-value 
                   (comp square k get-predicts) 
                   samples)
		         (mul mean mean))]
    {:mean mean
     :var var}))

(def smc-moments
  (empirical-moments smc-samples :states))
