;; **
;;; ### Posterior mean and variance from Kalman smoother
;; **

;; @@
(def true-moments 
  (time 
    (let [[mean covar _]
            (kalman-smoother +observations+
                             +obs-matrix+ +obs-cov+
                             +trans-matrix+ +trans-cov+
                             +init-mean+ +init-cov+)]
    {:mean (matrix mean)
     :var (matrix (map mat/diagonal covar))})))
