;; **
;;; ## Generative Model
;;; 
;;; @@ \begin{align}
;;; 	\v{x}\_0
;;;     &\sim
;;;     {\rm Norm}(\v{\mu}\_0, \v{V}\_0)
;;;     \\\\
;;;     \v{x}\_t
;;;     &=
;;;     \v{A} \cdot \v{x}\_{t-1}
;;; 	+
;;; 	\v{\delta}\_{t-1}
;;; 	&
;;; 	\v{\delta}\_{t-1}
;;;     &\sim
;;; 	{\rm Norm}(\v{0}, \v{C})
;;;     \\\\
;;;     \v{y}_t
;;;     &=
;;;     \v{C} \cdot \v{x}_t
;;; 	+
;;; 	\v{\epsilon}_t
;;;     &
;;; 	\v{\epsilon}_t
;;;     &\sim
;;; 	{\rm Norm}(\v{0}, \v{R})
;;; \end{align} @@
;;; 
;;; ## Parameters
;;; 
;;; - @@\v{\mu}_0@@: Mean for initial state
;;; - @@\v{V}_0@@: Covariance for initial state
;;; - @@\v{A}@@: Transition matrix
;;; - @@\v{Q}@@: Transition covariance
;;; - @@\v{C}@@: Observation matrix
;;; - @@\v{R}@@: Observation covariance
;;; 
;; **

;; **
;;; ## Simulate Data: Periodic motion in 2D latent space
;;; 
;;; For the purposes of our analysis we will look at simulated data, in which we assume that a more or less periodic motion in a two-dimensional latent space gives rise to a 20-dimensional signal in the observation space. The goal of inference is to infer the 2D latent space trajectory.
;;; 
;;; In order to enforce periodicity, we assume that the transition matrix @@A@@ is a simple rotation, whereas the the transition and observation covariance are assumed to be diagonal. Finally, the observation matrix is assumed to be somewhat sparse. 
;;; 
;;; @@ \begin{align}
;;;     \v{A}
;;;     &=
;;;     \left[
;;;         \begin{array}{cc}
;;;             \cos \omega
;;;             &
;;;             -\sin \omega
;;;             \\\\
;;;             \sin \omega
;;;             &
;;;             \cos \omega
;;;         \end{array}
;;;     \right]
;;;     &
;;;     \v{Q}
;;;     &=
;;;     q
;;;     \v{I}\_2
;;;     &
;;; 	\v{C}\_{1,2}
;;;     &\sim
;;;     {\rm Dirichlet}(0.1, \ldots, 0.1)
;;; 	&
;;; 	\v{R}
;;;     &=
;;;     r
;;;     \v{I}\_D
;;; \end{align} @@
;;; 
;;; 
;; **

;; @@
(defn rotation-matrix
  "A 2D rotation matrix"
  [omega]
  (mat/matrix [[(cos omega) (sin (- omega))]
   			   [(sin omega) (cos omega)]]))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/rotation-matrix</span>","value":"#'kalman/rotation-matrix"}
;; <=

;; @@
;; number of time points
(def +T+ 100)
;; dimension of observation space
(def +D+ 20)
;; dimension of latent space
(def +K+ 2)

;; parameters
(def +init-state+ [0. 1.])
(def +omega+ (/ (* 4.0 Math/PI) +T+))
(def +trans-matrix+ (rotation-matrix +omega+))
(def +q+ 0.01)
(def +trans-cov+ (mul +q+ (identity-matrix +K+)))
(def +obs-matrix+ (transpose (matrix (repeatedly 2 #(sample (dirichlet (repeat +D+ 0.1)))))))
(def +r+ 0.01)
(def +obs-cov+ (mul +r+ (identity-matrix +D+)))
(def +init-mean+ +init-state+)
(def +init-cov+ +trans-cov+)
(def +parameters+ [+init-mean+ +init-cov+
                   +obs-matrix+ +obs-cov+
                   +trans-matrix+ +trans-cov+])

 ;; sample state sequence and observations
(def +states+
  (let [trans-dist (mvn (zero-vector +K+) +trans-cov+)]
    (reduce (fn [states _]
              (conj states
                    (add (mmul +trans-matrix+ (peek states))
                         (sample trans-dist))))
            [+init-state+]
            (range 1 +T+))))

(def +observations+
   (let [obs-dist (mvn (zero-vector +D+) +obs-cov+)]
     (map (fn [state]
            (add (mmul +obs-matrix+ state)
                       (sample obs-dist)))
          +states+)))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/+observations+</span>","value":"#'kalman/+observations+"}
;; <=

;; **
;;; ### Plot 2D simulated latent state trajectory
;; **

;; @@
(defn get-limits 
  ([points pad]
  (let [xs (mat/reshape points [(mat/ecount points)])
        x-min (reduce min xs)
        x-max (reduce max xs)
        x-pad (* pad (- x-max x-min))]
    [(- x-min x-pad) (+ x-max x-pad)]))
   ([points]
    (get-limits points 0.1)))

(def +x-limits+ (get-limits +states+))
(def +y-limits+ (get-limits +observations+))

;; plot latent states
(plot/list-plot (map #(into [] %) +states+) 
                :joined true
                :color (first +plot-colors+)
                :plot-range [+x-limits+ +x-limits+])

