;; **
;;; ### Inference
;; **

;; @@
(def samples
  (->> (doquery :smc
             kalman
             [+observations+ 
              +obs-matrix+ +obs-cov+ 
              +trans-matrix+ +trans-cov+ 
              +init-mean+ +init-cov+]
             :number-of-particles 1000)
	   (take 10000)
       doall
       time))
